#include <iostream>
using namespace std;

int main(){
    int x,y;
    x=10;
    y=20;

    cout << "Tugas Operator Hubungan\n";
    cout << " \n";
    cout << "x = 10\n";
    cout << "y = 20\n";
    cout << " \n";

    cout << "x < y = " << (x<y) << endl;
    cout << "x > y = " << (x>y) << endl;
    
    cin.get();
    return 0;  
}